{
    "template_name": "Plantilla de Factura prueba",
    "company_id": 1,
    "column_mappings": {
        "ship_id": "ship_id",
        "peso": "weight",
        "peso_2": "weight 2",
        "altura": "height",
        "ancho": "width",
        "largo": "lenght",
        "coste": "cost",
        "coste_extra": "extra cost",
        "cliente": "client",
        "direccion": "direction",
        "codigo_postal": "cp",
        "pais": "country",
        "devuelto": "returned"
    },
    "formulas": [
        {
            "new_column": "real_weight",
            "formula": "(lenght * width * height * 167) / 1000000"
        }
    ],
    "validation_rules": [
        {
            "field": "country",
            "operator": "==",
            "value": "España",
            "conditions": [
                {
                    "field": "lenght",
                    "operator": ">",
                    "value": 120,
                    "highlight": "red"
                }
            ]
        },
        {
            "field": "country",
            "operator": "==",
            "value": "ES",
            "conditions": [
                {
                    "field": "weight",
                    "operator": ">",
                    "value": 50,
                    "highlight": "#09f"
                },
                {
                    "field": "weight",
                    "operator": ">=",
                    "value": 80,
                    "row_highlight": "purple"
                }
            ]
        },
        {
            "field": "lenght",
            "operator": ">=",
            "value": 150,
            "highlight": "yellow"
        },
        {
            "field": "real_weight",
            "operator": ">",
            "value": 120,
            "row_highlight": "#9f3"
        },
        {
            "duplicate_field": "ship_id",
            "row_highlight": "yellow"
        }
    ],
    "aggregations": [
        {
            "type": "sum",
            "fields": ["cost", "real_weight"]
        }
    ]
}

